mapper.select:
select count(*) from detail



			${field} like '%'||#{search}||'%'


			${field} like #{search}

select R.* from ( select rownum rnum, Q.* from (select * from detail
${field} like '%'||#{search}||'%'
${field} like #{search}
order by detail_id asc) Q where
rownum<=#{endNo}
) R where
rnum >= #{startNo}
