mapper.select:
select count(*) from plan



			${field} like '%'||#{search}||'%'


			${field} like #{search}

select R.* from ( select rownum rnum, Q.* from (select * from plan
${field} like '%'||#{search}||'%'
${field} like #{search}
order by plan_id desc) Q where
rownum<=#{endNo}
) R where
rnum >= #{startNo}
